import React, { useState } from 'react';
import '../styles/Inscription.css';

function Inscription() {
    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        email: '',
        telephone: '',
        password: '',
        confirmPassword: '',
    });
    const [errors, setErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        setErrors({ ...errors, [e.target.name]: '' });
    };

    const validate = () => {
        const newErrors = {};
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const phoneRegex = /^[0-9]{8,15}$/;

        if (!formData.nom.trim()) newErrors.nom = 'Le nom est requis.';
        if (!formData.prenom.trim()) newErrors.prenom = 'Le prénom est requis.';
        if (!formData.email.trim()) newErrors.email = 'L\'email est requis.';
        else if (!emailRegex.test(formData.email)) newErrors.email = 'Email invalide.';
        if (!formData.telephone.trim()) newErrors.telephone = 'Le téléphone est requis.';
        else if (!phoneRegex.test(formData.telephone)) newErrors.telephone = 'Numéro invalide.';
        if (!formData.password) newErrors.password = 'Mot de passe requis.';
        else if (formData.password.length < 6) newErrors.password = 'Min 6 caractères.';
        if (formData.password !== formData.confirmPassword)
            newErrors.confirmPassword = 'Les mots de passe ne correspondent pas.';

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validate()) {
            setSuccessMessage('Inscription réussie !');
            console.log('✔️ Données envoyées :', formData);
            setFormData({
                nom: '',
                prenom: '',
                email: '',
                telephone: '',
                password: '',
                confirmPassword: '',
            });
            setErrors({});
        }
    };

    return (
        <div className="wrapper">
            <div className="overlay">
                <div className="login-form">
                    <h1>Inscription Professionnelle</h1>
                    <form onSubmit={handleSubmit}>
                        {[
                            { label: 'Nom', name: 'nom', type: 'text' },
                            { label: 'Prénom', name: 'prenom', type: 'text' },
                            { label: 'Email', name: 'email', type: 'email' },
                            { label: 'Téléphone', name: 'telephone', type: 'tel' },
                            { label: 'Mot de passe', name: 'password', type: 'password' },
                            { label: 'Confirmer le mot de passe', name: 'confirmPassword', type: 'password' },
                        ].map(({ label, name, type }) => (
                            <div className="input-data" key={name}>
                                <input
                                    type={type}
                                    name={name}
                                    value={formData[name]}
                                    onChange={handleChange}
                                    required
                                />
                                <label>{label}</label>
                                <span className="underline"></span>
                                {errors[name] && <span className="error">{errors[name]}</span>}
                            </div>
                        ))}

                        <div className="btn">
                            <button type="submit">S'inscrire</button>
                        </div>

                        {successMessage && <p className="success">{successMessage}</p>}
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Inscription;
